mapper.select:
select *
from bkgroup
where group_no = #{ gNo }

mapper.update:
update bkgroup
set g_status = 'N'
where group_no = #{ gNo }

update files
set f_status = 'N'
where group_no = #{ gNo }

update bkgroup
set group_name = #{gName}, group_intro = #{gIntro}, group_total = #{gTotal},
	group_content = #{gContent}, gb_title = #{gbTitle}, gb_content = #{gbContent},
	group_date = #{gDate}, group_place = #{gPlace}  
where group_no = #{gNo}

update files
set origin_name = #{originName}, change_name = #{changeName}, upload_date = sysdate
where group_no = #{gNo}

mapper.select:
select *
from users
where user_no = #{ signUno }

mapper.update:
update group_sign
set gs_approval = 'Y'
where group_no = #{ gNo } and user_no = #{ uNo }

update group_sign
set gs_approval = 'N'
where group_no = #{ gNo } and user_no = #{ uNo }
